mapper.select:
SELECT m.user_no AS userNo, e.map_no AS mapNo, h.hotplace_no AS hotplaceNo, h.hotplace_name AS hotplaceName, AVG(e.review) AS avgReview, m.recom AS recomm, h.sido AS sido, h.gugun AS gugun, h.address AS address, h.lat AS lat, h.lng AS lng, AVG(COALESCE(e.emo_result, 0)) AS avgEmoResult, hc.hotplace_cate_no AS hotplaceCateNo, u.user_name AS userName FROM emoreview e JOIN hotplace h ON e.hotplace_no = h.hotplace_no JOIN mymap m ON e.map_no = m.map_no JOIN hotplace_cate hc ON h.hotplace_cate_no = hc.hotplace_cate_no JOIN user u ON m.user_no = u.user_no WHERE m.user_no = #{userNo}
AND (h.address LIKE CONCAT('%', #{search}, '%') OR h.hotplace_name LIKE CONCAT('%', #{search}, '%'))
GROUP BY m.user_no, e.map_no, h.hotplace_no, h.hotplace_name, m.recom, h.sido, h.gugun, h.address, h.lat, h.lng, hc.hotplace_cate_no, u.user_name ORDER BY hotplaceName ASC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM ( SELECT m.user_no AS userNo, e.map_no AS mapNo, h.hotplace_no AS hotplaceNo, h.hotplace_name AS hotplaceName, AVG(e.review) AS avgReview, m.recom AS recomm, h.sido AS sido, h.gugun AS gugun, h.address AS address, h.lat AS lat, h.lng AS lng, AVG(COALESCE(e.emo_result, 0)) AS avgEmoResult, hc.hotplace_cate_no AS hotplaceCateNo, u.user_name AS userName FROM emoreview e JOIN hotplace h ON e.hotplace_no = h.hotplace_no JOIN mymap m ON e.map_no = m.map_no JOIN hotplace_cate hc ON h.hotplace_cate_no = hc.hotplace_cate_no JOIN user u ON m.user_no = u.user_no WHERE m.user_no = #{userNo}
AND (h.address LIKE CONCAT('%', #{search}, '%') OR h.hotplace_name LIKE CONCAT('%', #{search}, '%'))
GROUP BY m.user_no, e.map_no, h.hotplace_no, h.hotplace_name, m.recom, h.sido, h.gugun, h.address, h.lat, h.lng, hc.hotplace_cate_no, u.user_name ) AS total_data
